import React, { Component } from 'react'
import '../css/ListeDerangement.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faTrashAlt, faSearch , faEllipsisV } from '@fortawesome/free-solid-svg-icons'
import {
  MenuItem,
  MenuDivider,
  ControlledMenu
} from '@szhsin/react-menu';
import '@szhsin/react-menu/dist/index.css';
export default class ListDerangementProbléme extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            listDer:[],
            searchValue:'',
            comptes:[],
            isOpenValue:[false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false,false]
        }
    }
    componentDidMount()
    {
       this.getPb()
       this.getCompte()
    }
//////////////////////////////////////////:
    getPb=()=>{
        fetch('http://localhost:5000/derangement/afficherPb/web/'+this.props.location.state.service, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          })
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({listDer:responseJson},()=>console.log(this.state.listDer))
             
            });
    }
    //////////////////////////////////////////::
    getCompte=()=>{
      if(this.props.location.state.service==="maintenance urbain")
      {
        this.setState({comptes:["Service d'équipement","service de gestion de qualité de service","Equipe T1_BEZ"]})
      }
      else{
        this.setState({comptes:["Service d'équipement","service de gestion de qualité de service","Equipe C7_BEZ","C8_BEZ"]})
      }
    }
    /////////////////////////////////////////
    delete=(id)=>{
      console.log(id) 
      fetch('http://localhost:5000/derangement/supprimerPb/web/'+id, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        
      })
        .then((response) => response.json())
        .then((responseJson) => {
           this.getPb()
         
        });
    }
    ///////////////////////////////////////////////
  send=(titre)=>{
    //recherche de l'indice de derangement qui a le titre dans le tableau 
    var i=0
    var found=false
    while(i<this.state.der && found==false)
    {
      if(this.state.der[i].titre===titre)
      {
        found=true
      }
      i++
    }
    return(
      <div style={{position:'relative',marginLeft:820}}>
      <ControlledMenu  isOpen={this.state.isOpenValue[i]} 
      onClose={() => closeList(i)}>
         <MenuItem >Envoyer</MenuItem>
         <MenuDivider />
         <MenuItem>Supprimer</MenuItem>
     </ControlledMenu>
     </div>
    )
  }
    ////////////////////////////////////////////////////
    closeList=(i)=>{
      let a=[...this.state.isOpenValue]
      a[i]=false
      this.setState({isOpenValue:a})
    }
    ///////////////////////////////
  render()
  {
      return(
        <div style={container}>
         <div style={{display:"inline-block"}}>
         <input type="text" onChange={this.onChangeSearch} placeholder=" chercher..." style={searchInput}/>
         <div style={SearchIconContainer}>
         <FontAwesomeIcon icon={faSearch} size="2.5x" style={IconStyle}/></div>
         </div>
         <div style={{position:'relative' , top:50, left:250, }}>
         <button style={btnStyle}  onClick={()=>this.props.history.push("/ListeAjouté",{service:"maintenance urbain"})}> Ajouté  </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeAttente",{service:"maintenance urbain"})}> En attente </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeTraite",{service:"maintenance urbain"})}> Traité </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeAbsent",{service:"maintenance urbain"})}> Client absent </button>
          <button style={btnStyleP}> Probléme  </button>
       </div> 
       <div style={HeaderList}>
         <input type="checkbox" style={checkBoxStyle}/>
         <p style={Pstyle}>Titre</p>
         <p style={Dstyle}>Date de signalisation</p>
         <FontAwesomeIcon icon={faEllipsisV} size="2.5x" style={IconHeaderStyle} onClick={() => this.setState({isOpen:true})}/>
         <div style={{position:'relative',marginLeft:820}}>
         <ControlledMenu  isOpen={this.state.isOpen} 
         onClose={() => this.setState({isOpen:false})}>
            <MenuItem >Envoyer</MenuItem>
            <MenuDivider />
            <MenuItem>Supprimer</MenuItem>
        </ControlledMenu>
        </div>
        </div>
        <div style={listeContainer}>
          {this.state.listDer.map((der)=>{
             return(
                 <div>
                 <input type="checkbox" style={checkBoxStyle} Onchange={()=>this.select(der.titre,der._id)}/>
                 <div style={titreELTStyle}><p>{der.titre}</p></div>
                 <div style={dateELTStyle}><p>{der.dateSignal}</p></div>
                 <button style={buttonELTStyle} onClick={()=>{this.send(der.titre)}}>Envoyer</button>
                 <FontAwesomeIcon icon={faTrashAlt} size="2.5x" style={iconELTStyle} onClick={()=>{this.delete(der._id)}}/>
                 </div>
                 
               )
          })}
        </div>
        </div>
      )
  }
};
 const container={
   left:"20%",
   backgroundColor:'#F8F8F8', 
   height:'100vh',
   width:"80%",
   position:"fixed"
  }
 const searchInput={
  position:'relative',
  border:"none",
  borderWidth:0.5,
  borderRadius:'0px 15px 15px 0px',
  height:30,
  width:220,
  left:450,
  top:40,
  outline:'none',
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" 
}
const SearchIconContainer={
  position:'relative',
  height:30,
  width:60,
  left:390,
  top:10,
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  borderRadius:'15px 0px 0px 15px',
 }
 const IconStyle={ 
 color: '#455956',
 position:'relative',
 left:25,
 top:7, 
  }
 const btnStyleP={
  background:"#27B8B8",
  color: "white",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const btnStyle={
  background:"white",
  color: "#27B8B8",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const HeaderList={
  display: 'inline-block',
  flexDirection: 'row',
  backgroundColor:"#E1DADA",
  position:'relative',
  top:100,
  left:50,
  width:"90%",
  borderRadius:10,
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" 
 }
 const checkBoxStyle={
   height:15,
   width:15,
   display: 'inline-block',
   marginLeft:20,
 }
 const Pstyle={
  display: 'inline-block',
  fontFamily: 'roboto',
  marginLeft:160,
  color:"#54545A"
 }
 const Dstyle={
  display: 'inline-block',
  fontFamily: 'roboto',
  marginLeft:210,
  color:"#54545A"
 }
 const IconHeaderStyle={
  marginLeft:350,
  color:"#54545A",
  cursor:"pointer",
 }
 const listeContainer={
  display: 'inline-block',
  flexDirection: 'row',
  backgroundColor:"white",
  position:'relative',
  top:100,
  left:50,
  height:400,
  width:"90%",
  borderRadius:10,
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" ,
  overflowY: 'auto'
 }
 const titreELTStyle={
  display: 'inline-block',
  marginLeft:120,
  fontFamily: 'roboto',
  color:"#54545A",
  fontSize:14,
  width:100
 }
 const dateELTStyle={
  display: 'inline-block',
  marginLeft:170,
  fontFamily: 'roboto',
  color:"#54545A",
  fontSize:14,
  width:250
 }
 const buttonELTStyle={
  display: 'inline-block',
  background:"#27B8B8",
  color: "white",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  marginLeft:150,
  outline:'none',
  width:100
 }
 const iconELTStyle={
  display: 'inline-block',
  marginLeft:10,
  cursor:"pointer",
 }
