import React, { Component } from 'react'
import '../css/ListeDerangement.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faTrashAlt, faSearch , faEllipsisV } from '@fortawesome/free-solid-svg-icons'
import {
  MenuItem,
  MenuDivider,
  ControlledMenu
} from '@szhsin/react-menu';
import '@szhsin/react-menu/dist/index.css';
export default class ListDerangementAbsent extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            listDer:[],
            searchValue:''
        }
    }
    componentDidMount()
    {
       this.getAbsent()
    }
//////////////////////////////////////////:
    getAbsent=()=>{
        fetch('http://localhost:5000/derangement/afficherAbsent/web/'+this.props.location.state.service, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          })
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({listDer:responseJson},()=>console.log(this.state.listDer))
             
            });
    }
    //////////////////////////////////////////::
    onChangeSearch=(e)=>{
        this.setState({searchValue:e.target.value })
    }
    ////////////////////////////////
    delete=(id)=>{
      console.log(id) 
      fetch('http://localhost:5000/derangement/supprimerAbsent/web/'+id, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        
      })
        .then((response) => response.json())
        .then((responseJson) => {
           this.getAbsent()
         
        });
    }
    send=(id)=>{
      console.log(id)
      fetch('http://localhost:5000/derangement/envoyerAbsentTech/web/'+id, {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        })
          .then((response) => response.json())
          .then((responseJson) => {
            this.getAbsent()
           
          });
  }
    ///////////////////////////////////////////////
  render()
  {
      return(
        <div style={container}>
         <div style={{display:"inline-block"}}>
         <input type="text" onChange={this.onChangeSearch} placeholder=" chercher..." style={searchInput}/>
         <div style={SearchIconContainer}>
         <FontAwesomeIcon icon={faSearch} size="2.5x" style={IconStyle}/></div>
         </div>
         <div style={{position:'relative' , top:50, left:250, }}>
         <button style={btnStyle}  onClick={()=>this.props.history.push("/ListeAjouté",{service:this.props.location.state.service})}> Ajouté  </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeAttente",{service:this.props.location.state.service})}> En attente </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeTraite",{service:this.props.location.state.service})}> Traité </button>
          <button style={btnStyleP}> Client absent </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeProbléme",{service:this.props.location.state.service})}> Probléme  </button>
       </div> 
       <div style={HeaderList}>
         <input type="checkbox" style={checkBoxStyle}/>
         <p style={Pstyle}>Titre</p>
         <p style={Dstyle}>Date de signalisation</p>
         <FontAwesomeIcon icon={faEllipsisV} size="2.5x" style={IconHeaderStyle} onClick={() => this.setState({isOpen:true})}/>
         <div style={{position:'relative',marginLeft:820}}>
         <ControlledMenu  isOpen={this.state.isOpen} style={{fontFamily: 'roboto'}}
         onClose={() => this.setState({isOpen:false})}>
            <MenuItem >Envoyer</MenuItem>
            <MenuDivider />
            <MenuItem>Supprimer</MenuItem>
        </ControlledMenu>
        </div>
        </div>
        <div style={listeContainer}>
          {this.state.listDer.map((der)=>{
               return(
                 <div>
                 <input type="checkbox" style={checkBoxStyle} Onchange={()=>this.select(der._id)}/>
                 <div style={titreELTStyle}><p>{der.titre}</p></div>
                 <div style={dateELTStyle}><p>{der.dateSignal}</p></div>
                 <button style={buttonELTStyle} onClick={()=>{this.send(der.titre)}}>Envoyer</button>
                 <FontAwesomeIcon icon={faTrashAlt} size="2.5x" style={iconELTStyle} onClick={()=>{this.delete(der._id)}}/>
                 </div>
                 
               )
          })}
        </div>
        </div>
      )
  }
};
 const container={
   left:"20%",
   backgroundColor:'#F8F8F8', 
   height:'100vh',
   width:"80%",
   position:"fixed"
  }
 const searchInput={
  position:'relative',
  border:"none",
  borderWidth:0.5,
  borderRadius:'0px 15px 15px 0px',
  height:30,
  width:220,
  left:450,
  top:40,
  outline:'none',
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" 
}
const SearchIconContainer={
  position:'relative',
  height:30,
  width:60,
  left:390,
  top:10,
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  borderRadius:'15px 0px 0px 15px',
 }
 const IconStyle={ 
 color: '#455956',
 position:'relative',
 left:25,
 top:7, 
  }
 const btnStyleP={
  background:"#27B8B8",
  color: "white",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const btnStyle={
  background:"white",
  color: "#27B8B8",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const HeaderList={
  display: 'inline-block',
  flexDirection: 'row',
  backgroundColor:"#E1DADA",
  position:'relative',
  top:100,
  left:50,
  width:"90%",
  borderRadius:10,
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" 
 }
 const checkBoxStyle={
   height:15,
   width:15,
   display: 'inline-block',
   marginLeft:20,
 }
 const Pstyle={
  display: 'inline-block',
  fontFamily: 'roboto',
  marginLeft:160,
  color:"#54545A"
 }
 const Dstyle={
  display: 'inline-block',
  fontFamily: 'roboto',
  marginLeft:210,
  color:"#54545A"
 }
 const IconHeaderStyle={
  marginLeft:350,
  color:"#54545A",
  cursor:"pointer",
 }
 const listeContainer={
  display: 'inline-block',
  flexDirection: 'row',
  backgroundColor:"white",
  position:'relative',
  top:100,
  left:50,
  height:400,
  width:"90%",
  borderRadius:10,
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" ,
  overflowY: 'auto'
 }
 const titreELTStyle={
  display: 'inline-block',
  marginLeft:120,
  fontFamily: 'roboto',
  color:"#54545A",
  fontSize:14,
  width:100
 }
 const dateELTStyle={
  display: 'inline-block',
  marginLeft:170,
  fontFamily: 'roboto',
  color:"#54545A",
  fontSize:14,
  width:250
 }
 const buttonELTStyle={
  display: 'inline-block',
  background:"#27B8B8",
  color: "white",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  marginLeft:150,
  outline:'none',
  width:100
 }
 const iconELTStyle={
  display: 'inline-block',
  marginLeft:10,
  cursor:"pointer",
 }
