import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSearch } from "@fortawesome/free-solid-svg-icons";
import HomeIcon from "@material-ui/icons/Home";
import ListIcon from "@material-ui/icons/List";
import AddBoxIcon from "@material-ui/icons/AddBox";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import logo from "../assets/image/logo.png";
import "../assets/stylesList.css";

export default class ListDerangementAttente extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listDer: [],
      SearchedValue: "",
      SearchedList: [],
      isOpen: false,
      data: [
        {
          title: "Acceuil",
          icon: <HomeIcon />,
          link: "/ListeAttente",
        },
        {
          title: "Ajouter dérangement",
          icon: <AddBoxIcon />,
          link: "/AjouterDer",
        },

        {
          title: "Liste des PCs",
          icon: <ListIcon />,
          link: "/EtatPc",
        },

        {
          title: "Ajouter PC",
          icon: <AddBoxIcon />,
          link: "/AjouterPc",
        },
        {
          title: "Déconnecter",
          icon: <ExitToAppIcon />,
          link: "/",
        },
      ],
    };
  }
  componentDidMount() {
    this.getAttente();
    console.log(this.props.location.state.service);
  }
  //////////////////////////////////////////:
  getAttente = () => {
    fetch(
      "http://localhost:5000/derangement/afficherAttente/web/" +
        this.props.location.state.service,
      {
        method: "GET",
        headers: {
          "Content-Type": "application/json",
        },
      }
    )
      .then((response) => response.json())
      .then((responseJson) => {
        this.setState({ listDer: responseJson }, () =>
          console.log(this.state.listDer)
        );
        this.setState({ SearchedList: responseJson }, () =>
          console.log(this.state.SearchedList)
        );
      });
  };
  //////////////////////////////////////////::
  search = () => {
    if (this.state.SearchedValue === "") {
      this.setState({ SearchedList: this.state.listDer });
    } else {
      var i;
      var a = [];
      for (i = 0; i < this.state.listDer.length; i++) {
        if (
          this.state.listDer[i].titre === this.state.SearchedValue ||
          this.state.listDer[i].nomClient === this.state.SearchedValue ||
          this.state.listDer[i].typeDerang === this.state.SearchedValue ||
          this.state.listDer[i].nomTech === this.state.SearchedValue ||
          this.state.listDer[i].numFixClient === this.state.SearchedValue ||
          this.state.listDer[i].addressClient === this.state.SearchedValue
        ) {
          a.push(this.state.listDer[i]);
        }
      }
      this.setState({ SearchedList: a });
    }
  };

  ///////////////////////////////////////////////
  render() {
    return (
      <div>
        <img
          src={logo}
          alt="img"
          style={{
            marginTop: 10,
            marginLeft: 20,
            height: 60,
            width: 120,
            position: "fixed",
          }}
        />
        <div className="sidebar">
          <ul className="sidebarList">
            {this.state.data.map((val, key) => {
              return (
                <li
                  className="row"
                  key={key}
                  onClick={() => {
                    this.props.history.push(val.link, {
                      service: this.props.location.state.service,
                    });
                  }}
                >
                  <div id="icon">{val.icon}</div>
                  <div id="title">{val.title}</div>
                </li>
              );
            })}
          </ul>
        </div>

        <div style={container}>
          <div style={{ display: "inline-block" }}>
            <div style={SearchIconContainer}>
              <FontAwesomeIcon icon={faSearch} size="2.5x" style={IconStyle} />
            </div>
          </div>
          <input
            type="text"
            placeholder=" chercher..."
            style={searchInput}
            onChange={(event) => {
              this.setState({ SearchedValue: event.target.value });
            }}
            onKeyPress={(event) => {
              if (event.key === "Enter") {
                this.search();
              }
            }}
          />

          <div style={{ position: "relative", top: 50, left: 250 }}>
            <button
              style={btnStyle}
              onClick={() =>
                this.props.history.push("/ListeAjouté", {
                  service: this.props.location.state.service,
                })
              }
            >
              {" "}
              Ajouté{" "}
            </button>
            <button style={btnStyleP}> En attente </button>
            <button
              style={btnStyle}
              onClick={() =>
                this.props.history.push("/ListeTraite", {
                  service: this.props.location.state.service,
                })
              }
            >
              {" "}
              Traité{" "}
            </button>
            <button
              style={btnStyle}
              onClick={() =>
                this.props.history.push("/ListeAbsent", {
                  service: this.props.location.state.service,
                })
              }
            >
              {" "}
              Client absent{" "}
            </button>
            <button
              style={btnStyle}
              onClick={() =>
                this.props.history.push("/ListeProbléme", {
                  service: this.props.location.state.service,
                })
              }
            >
              {" "}
              Probléme{" "}
            </button>
          </div>
          <div style={HeaderList}>
            <p style={Pstyle}>Titre</p>
            <p style={Dstyle}>Date de signalisation</p>
            <p style={Pstyle}>Nom de l'équipe technicien</p>
          </div>
          <div style={listeContainer}>
            {this.state.SearchedList.map((der) => {
              return (
                <div
                  style={{ cursor: "pointer" }}
                  onClick={() =>
                    this.props.history.push("/InfoDerAttente", {
                      titre: der.titre,
                      service: this.props.location.state.service,
                    })
                  }
                >
                  <div style={textELTStyle}>
                    <p>{der.titre}</p>
                  </div>
                  <div style={dateELTStyle}>
                    <p>{der.dateSignal}</p>
                  </div>
                  <div style={textELTStyle}>
                    <p> {der.nomTech}</p>
                  </div>
                  <div style={space}></div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    );
  }
}
