import React, { Component } from 'react'
import '../css/ListeDerangement.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
export default class ListDerangemet extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            listDer:[],
            searchValue:''
        }
    }
    componentDidMount()
    {
       this.getAttente()
    }
//////////////////////////////////////////:
    getAttente=()=>{
        fetch('http://localhost:5000/derangement/afficherAttente/web/'+this.props.location.state.service, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          })
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({listDer:responseJson},()=>console.log(this.state.listDer))
             
            });
    }
    //////////////////////////////////////////::
    onChangeSearch=(e)=>{
        this.setState({searchValue:e.target.value })
    }
  render()
  {
      return(
        <div style={{left:"20%",backgroundColor:'#F8F8F8', height:'100vh',width:"80%", position:"fixed",alignItems:"center"}}>
         <div style={{display:"inline-block"}}>
         <input type="text" onChange={this.onChangeSearch} 
         style={{border:"none",
         borderWidth:0.5,
         borderRadius:5,
         height:20,
         width:240,
         boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" }}/>
         <FontAwesomeIcon icon={['fal',"search"]} size="2.5x" style={{ color: 'black' }}/>
         </div>
        </div>
      )
  }
};
