import React, { Component } from 'react'
import '../css/ListeDerangement.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faTrashAlt, faSearch , faEllipsisV } from '@fortawesome/free-solid-svg-icons'
import {
  MenuItem,
  MenuDivider,
  ControlledMenu,
  Menu
} from '@szhsin/react-menu';
import '@szhsin/react-menu/dist/index.css'
export default class ListDerangementProbléme extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            listDer:[],
            SearchedValue:"",
            SearchedList:[],
            comptes:[],
       }
    }
    componentDidMount()
    {
       this.getPb()
       this.getCompte()
       console.log(this.props.location.state.service)
       console.log(this.state.comptes)
    }
//////////////////////////////////////////:
    getPb=()=>{
        fetch('http://localhost:5000/derangement/afficherPb/web/'+this.props.location.state.service, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          })
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({listDer:responseJson},()=>console.log(this.state.listDer))
             
            });
    }
    //////////////////////////////////////////::
    getCompte=()=>{
      if(this.props.location.state.service==="maintenance urbain")
      {
        this.setState({comptes:["équipement","gestion de qualité de service","T1_BEZ"]})
      }
      else{
        this.setState({comptes:["équipement","gestion de qualité de service","C7_BEZ","C8_BEZ"]})
      }
    }
    /////////////////////////////////////////
    delete=(id)=>{
      console.log(id) 
      fetch('http://localhost:5000/derangement/supprimerPb/web/'+id, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        
      })
        .then((response) => response.json())
        .then((responseJson) => {
           this.getPb()
         
        });
    }
    ///////////////////////////////////////////////
  send=(titre,destination)=>{
   console.log(titre)
   console.log(destination)
   if(this.props.location.state.service==="maintenance urbain")
   {
    fetch('http://localhost:5000/derangement/EnvoiePbMain/web', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        titre:titre,
        destination:destination
      }),
    })
      .then((response) => response.json())
      .then((responseJson) => {
         this.getPb()
         console.log(responseJson)
      });
   }
   else{
    fetch('http://localhost:5000/derangement/EnvoiePbQua/web', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        titre:titre,
        destination:destination
      }),
    })
      .then((response) => response.json())
      .then((responseJson) => {
         this.getPb()
         console.log(responseJson)
       
      });
   }
  }
  
    ///////////////////////////////
    search=()=>{
      if(this.state.SearchedValue==="")
      {
        this.setState({SearchedList:this.state.listDer})
      }
      else{
        var i
        var a=[]
        for(i=0;i<this.state.listDer.length;i++)
        {
          if(this.state.listDer[i].titre===this.state.SearchedValue ||
            this.state.listDer[i].nomClient===this.state.SearchedValue ||
            this.state.listDer[i].typeDerang===this.state.SearchedValue ||
            this.state.listDer[i].nomTech===this.state.SearchedValue ||
            this.state.listDer[i].numFixClient===this.state.SearchedValue ||
            this.state.listDer[i].addressClient===this.state.SearchedValue )
            {
              a.push(this.state.listDer[i])
            }
        }
        this.setState({SearchedList:a})
      }
   }
  /////////////////////////////////////////
  render()
  {
      return(
        <div style={container}>
         <div style={{display:"inline-block"}}>
         <input type="text"  placeholder=" chercher..." style={searchInput} onChange={event => {this.setState({SearchedValue: event.target.value})}}
         onKeyPress={event => {
                     if (event.key === 'Enter') {
                       this.search()
                     }
                   }}/>
         <div style={SearchIconContainer}>
         <FontAwesomeIcon icon={faSearch} size="2.5x" style={IconStyle}/></div>
         </div>
         <div style={{position:'relative' , top:50, left:250, }}>
         <button style={btnStyle}  onClick={()=>this.props.history.push("/ListeAjouté",{service:this.props.location.state.service})}> Ajouté  </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeAttente",{service:this.props.location.state.service})}> En attente </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeTraite",{service:this.props.location.state.service})}> Traité </button>
          <button style={btnStyle} onClick={()=>this.props.history.push("/ListeAbsent",{service:this.props.location.state.service})}> Client absent </button>
          <button style={btnStyleP}> Probléme  </button>
       </div> 
       <div style={HeaderList}>
         <input type="checkbox" style={checkBoxStyle}/>
         <p style={Pstyle}>Titre</p>
         <p style={Dstyle}>Date de signalisation</p>
         <FontAwesomeIcon icon={faEllipsisV} size="2.5x" style={IconHeaderStyle} onClick={() => this.setState({isOpen:true})}/>
         <div style={{position:'relative',marginLeft:820}}>
         <ControlledMenu  isOpen={this.state.isOpen} 
         onClose={() => this.setState({isOpen:false})}>
            <MenuItem >Envoyer</MenuItem>
            <MenuDivider />
            <MenuItem>Supprimer</MenuItem>
        </ControlledMenu>
        </div>
        </div>
        <div style={listeContainer}>
          {this.state.listDer.map((der)=>{
             return(
                 <div>
                 <input type="checkbox" style={checkBoxStyle}/>
                 <div style={titreELTStyle}><p>{der.titre}</p></div>
                 <div style={dateELTStyle}><p>{der.dateSignal}</p></div>
                 <Menu  direction="left" menuButton={<button style={buttonELTStyle} >Envoyer</button>}>
                  {this.state.comptes.map((item)=>{
                    return(
                      <MenuItem style={{fontFamily: 'roboto'}} onClick={()=>{this.send(der.titre,item)}}>{item}</MenuItem>
                    )
                  })}
                 </Menu>
                 <FontAwesomeIcon icon={faTrashAlt} size="2.5x" style={iconELTStyle} onClick={()=>{this.delete(der._id)}}/>
                 </div>
                 
               )
          })}
        </div>
        </div>
      )
  }
};
 const container={
   left:"20%",
   backgroundColor:'#F8F8F8', 
   height:'100vh',
   width:"80%",
   position:"fixed"
  }
 const searchInput={
  position:'relative',
  border:"none",
  borderWidth:0.5,
  borderRadius:'0px 15px 15px 0px',
  height:30,
  width:220,
  left:450,
  top:40,
  outline:'none',
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" 
}
const SearchIconContainer={
  position:'relative',
  height:30,
  width:60,
  left:390,
  top:10,
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  borderRadius:'15px 0px 0px 15px',
 }
 const IconStyle={ 
 color: '#455956',
 position:'relative',
 left:25,
 top:7, 
  }
 const btnStyleP={
  background:"#27B8B8",
  color: "white",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const btnStyle={
  background:"white",
  color: "#27B8B8",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const HeaderList={
  display: 'inline-block',
  flexDirection: 'row',
