import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faTrashAlt,
  faSearch,
  faEllipsisV,
} from "@fortawesome/free-solid-svg-icons";
import {
  MenuItem,
  MenuDivider,
  ControlledMenu,
  Menu,
  SubMenu,
} from "@szhsin/react-menu";
import "@szhsin/react-menu/dist/index.css";
import HomeIcon from "@material-ui/icons/Home";
import ListIcon from "@material-ui/icons/List";
import AddBoxIcon from "@material-ui/icons/AddBox";
import ExitToAppIcon from "@material-ui/icons/ExitToApp";
import logo from "../assets/image/logo.png";
import "../assets/stylesList.css";
export default class ListDerangementProbléme extends Component {
  constructor(props) {
    super(props);
    this.state = {
      listDer: [],
      SearchedValue: "",
      SearchedList: [],
      comptes: [],
      checkedAll: false,
      checkedItems: [],
      data: [
        {
          title: "Acceuil",
          icon: <HomeIcon />,
          link: "/ListeAttente",
        },
        {
          title: "Ajouter dérangement",
          icon: <AddBoxIcon />,
          link: "/AjouterDer",
        },

        {
          title: "Liste des PCs",
          icon: <ListIcon />,
          link: "/EtatPc",
        },

        {
          title: "Ajouter PC",
          icon: <AddBoxIcon />,
          link: "/AjouterPc",
        },
        {
          title: "Déconnecter",
          icon: <ExitToAppIcon />,
          link: "/",
        },
      ],
    };
  }
  componentDidMount() {
    this.getPb();
    this.getCompte();
  }
  //////////////////////////////////////////:
  getPb = () => {
    fetch(
      "http://localhost:5000/derangement/afficherPb/web/" +
