import React, { Component } from 'react'
import '../css/ListeDerangement.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faCoffee, faSearch } from '@fortawesome/free-solid-svg-icons'
export default class ListDerangemet extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            listDer:[],
            searchValue:''
        }
    }
    // componentDidMount()
    // {
    //    this.getAttente()
    // }
//////////////////////////////////////////:
    getAttente=()=>{
        fetch('http://localhost:5000/derangement/afficherAttente/web/'+this.props.location.state.service, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          })
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({listDer:responseJson},()=>console.log(this.state.listDer))
             
            });
    }
    //////////////////////////////////////////::
    onChangeSearch=(e)=>{
        this.setState({searchValue:e.target.value })
    }
  render()
  {
      return(
        <div style={{left:"20%",backgroundColor:'#F8F8F8', height:'100vh',width:"80%", position:"fixed"}}>
         <div style={{display:"inline-block",alignItems:"center",justifyContent:"center"}}>
         <input type="text" onChange={this.onChangeSearch} placeholder="chercher..."
         style={{
         position:'relative',
         border:"none",
         borderWidth:0.5,
         borderRadius:15,
         height:30,
         width:260,
         left:380,
         top:40,
         boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" }}/>
         <FontAwesomeIcon icon={faSearch} size="2.5x" style={{ color: 'black',
         position:'relative',
         left:80,
         top:40, }}/>
         </div>
        </div>
      )
  }
};
