import React, { Component } from 'react'
import '../css/ListeDerangement.css'
import {FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faCoffee, faSearch } from '@fortawesome/free-solid-svg-icons'
export default class ListDerangementAttente extends Component{
    constructor(props)
    {
        super(props)
        this.state={
            listDer:[],
            searchValue:''
        }
    }
    // componentDidMount()
    // {
    //    this.getAttente()
    // }
//////////////////////////////////////////:
    getAttente=()=>{
        fetch('http://localhost:5000/derangement/afficherAttente/web/'+this.props.location.state.service, {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
            },
          })
            .then((response) => response.json())
            .then((responseJson) => {
                this.setState({listDer:responseJson},()=>console.log(this.state.listDer))
             
            });
    }
    //////////////////////////////////////////::
    onChangeSearch=(e)=>{
        this.setState({searchValue:e.target.value })
    }
    ///////////////////////////////////////////////
  render()
  {
      return(
        <div style={container}>
         <div style={{display:"inline-block"}}>
         <input type="text" onChange={this.onChangeSearch} placeholder=" chercher..." style={searchInput}/>
         <div style={SearchIconContainer}>
         <FontAwesomeIcon icon={faSearch} size="2.5x" style={IconStyle}/></div>
         </div>
         <div style={{position:'relative' , top:50, left:250, }}>
          <button style={btnStyleP}> En attente </button>
          <button style={btnStyle}  onClick={()=>this.props.history.push("/ListeTraite",{service:"maintenance urbain"})}> Traité </button>
          <button style={btnStyle}  onClick={()=>this.props.history.push("/ListeAbsent",{service:"maintenance urbain"})}> Client absent </button>
          <button style={btnStyle}  onClick={()=>this.props.history.push("/ListeProbléme",{service:"maintenance urbain"})}> Probléme  </button>
        </div> 
        <div style={HeaderList}>
         <input type="checkbox" style={checkBoxStyle}/>
         <p>Titre</p>
         <p>Date de signalisation</p>
        </div>
        </div>
      )
  }
};
 const container={
   left:"20%",
   backgroundColor:'#F8F8F8', 
   height:'100vh',
   width:"80%",
   position:"fixed"
  }
 const searchInput={
  position:'relative',
  border:"none",
  borderWidth:0.5,
  borderRadius:'0px 15px 15px 0px',
  height:30,
  width:220,
  left:420,
  top:40,
  outline:'none',
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)" 
}
const SearchIconContainer={
  position:'relative',
  height:30,
  width:60,
  left:360,
  top:10,
  backgroundColor:'white',
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  borderRadius:'15px 0px 0px 15px',
 }
 const IconStyle={ 
 color: '#455956',
 position:'relative',
 left:25,
 top:7, 
  }
 const btnStyleP={
  background:"#27B8B8",
  color: "white",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const btnStyle={
  background:"white",
  color: "#27B8B8",
  border:"none",
  padding:"5px 10px",
  borderRadius:10,
  cursor:"pointer",
  boxShadow: "0px 8px 20px 0px rgba(0, 0, 0, 0.15)",
  marginLeft:20,
  outline:'none',
  width:100
 }
 const HeaderList={
  display:"inline-block",
  flexDirection: 'row',
  backgroundColor:"#E1DADA",
  position:'relative',
  top:100,
  left:50,
  width:"90%"
 }
 const checkBoxStyle={
   height:5,
   width:5
 }
